'use client';

import { useAuth } from '@/lib/auth-context';
import { useRouter } from 'next/navigation';
import { useEffect } from 'react';

export default function Dashboard() {
  const { user, logout, loading } = useAuth();
  const router = useRouter();

  useEffect(() => {
    if (!loading && !user) {
      router.push('/login');
    }
  }, [user, loading, router]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return null;
  }

  const handleLogout = async () => {
    try {
      await logout();
      router.push('/');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h1 className="text-xl font-semibold text-gray-900">
                üåç Travel Planner
              </h1>
            </div>
            <div className="flex items-center space-x-4">
              <span className="text-gray-700">Welcome, {user.displayName}</span>
              <button
                onClick={handleLogout}
                className="btn-secondary"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </nav>

      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="text-center">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">
              Welcome to your Dashboard!
            </h2>
            <p className="text-gray-600 mb-8">
              You're successfully logged in. Trip planning features coming soon!
            </p>
            
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-4xl mx-auto">
              <div className="card">
                <div className="text-4xl mb-4">üó∫Ô∏è</div>
                <h3 className="text-xl font-semibold mb-2">My Trips</h3>
                <p className="text-gray-600 mb-4">
                  Create and manage your travel itineraries
                </p>
                <button className="btn-primary w-full" disabled>
                  Coming Soon
                </button>
              </div>
              
              <div className="card">
                <div className="text-4xl mb-4">üìç</div>
                <h3 className="text-xl font-semibold mb-2">Destinations</h3>
                <p className="text-gray-600 mb-4">
                  Add places to visit with maps and details
                </p>
                <button className="btn-primary w-full" disabled>
                  Coming Soon
                </button>
              </div>
              
              <div className="card">
                <div className="text-4xl mb-4">üîó</div>
                <h3 className="text-xl font-semibold mb-2">Share Trips</h3>
                <p className="text-gray-600 mb-4">
                  Share your itineraries with friends
                </p>
                <button className="btn-primary w-full" disabled>
                  Coming Soon
                </button>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}
