commits.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  html {
    @apply h-full;
  }
  
  body {
    @apply h-full bg-gray-50 text-gray-900 antialiased;
  }
}

@layer components {
  .btn-primary {
    @apply bg-primary-600 text-white px-4 py-2 rounded-xl font-medium hover:bg-primary-700 transition-colors;
  }
  
  .btn-secondary {
    @apply bg-white text-gray-700 px-4 py-2 rounded-xl font-medium border border-gray-300 hover:bg-gray-50 transition-colors;
  }
  
  .card {
    @apply bg-white rounded-xl shadow-sm border border-gray-200 p-6;
  }
  
  .input-field {
    @apply w-full px-3 py-2 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent;
  }
  
  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
}

/* Mapbox styles */
.mapboxgl-popup {
  max-width: 300px;
}

.mapboxgl-popup-content {
  @apply rounded-xl shadow-lg;
}

.mapboxgl-popup-close-button {
  @apply text-gray-400 hover:text-gray-600;
}
