+  const toggleDay = (day: number) => {
+    const newExpanded = new Set(expandedDays);
+    if (newExpanded.has(day)) {
+      newExpanded.delete(day);
+    } else {
+      newExpanded.add(day);
+    }
+    setExpandedDays(newExpanded);
+    onDaySelect(day);
