+          // Create updated destinations with proper renumbering
+          const updatedDestinations = allDestinations.map(dest => {
+            if (dest.id === draggedDestination.id) {
+              return {
+                ...dest,
+                day: targetDay,
+                orderIndex: newOrderIndex
+              };
