+        
+        // Find the new sequential index
+        const newIndex = dayDestinations.findIndex(d => d.id === dest.id) + 1;
+        
+        return {
+          ...dest,
+          orderIndex: newIndex
+        };
+      }
+      return dest;
+    });
+  };
